let credits = { 
    "student_names": "Patricia Diemunsch, Refael Blatt", 
    "recode_artist": "Karl Gerstner", 
    "recode_title": "Diagon rot/grün", 
    "recode_year": 1981 
}

/* For even more fun use 0 – 9 and the enter key */

function setup() {
	createCanvas(windowWidth, windowHeight);

	setupAudio();
	setupAudio();
}


function draw() {
	/* audio vars: amp, ampEase, fft, waveform */
	updateAudio();

/* KEY INTERACTOIN */
	if(keyIsDown(49)) {
		background(200, 50, 100);
	} else if(keyIsDown(50)) {
		background(10, 110, 115);
	} else if(keyIsDown(51)) {
		background(100, 30, 200);
	} else if(keyIsDown(52)) {
		background(255, 220, 97);
	} else if(keyIsDown(53)) {
		background(0);
	} else if(keyIsDown(54)) {
		background(255);
	} else if(keyIsDown(55)) {
		background(0, 30, 200);
	} else if(keyIsDown(56)) {
		background(100, 30, 200);
	} else if(keyIsDown(57)) {
		background(200, 100, 40);
	} else if(keyIsDown(48)) {
		background(20, 200, 50);
	} else if(keyIsDown(13)) {
	} else {
		background(0, 80, 115);
	}

	strokeWeight(1);
	stroke(255)

	let lineCount = 50;
	let yVol = ampEase * 2.5;

	/* QUADS */
	for(let i = 0; i < lineCount; i++) {
		let mapY = map(1, 0, lineCount - 1, 0, width); // aligns the quads to line

		push();
		fill(21, 150, 181)
		noStroke();
		translate(i * mapY, i * lineCount * yVol % height); // spread them 
		quad(0*yVol, height / 3, 0*yVol, height / 7, mapY*yVol, height / 20, mapY*yVol, height / 4);
		pop();

		push();
		fill(150, 110, 170)
		noStroke();
		translate(i * mapY, i * lineCount * yVol % height); // spread them 
		quad(0*yVol, height / 2, 0*yVol, height / 3, mapY*yVol, height / 4, mapY*yVol, height / 2.5);
		pop();

		push();
		fill(249, 78, 60)
		noStroke();
		translate(i * mapY, i * lineCount * yVol % height); // spread them 
		quad(0*yVol, height * .6, 0*yVol, height / 2, mapY*yVol, height / 2.5, mapY*yVol, height * .5);
		pop();
	}

	/* LINES */
	for(let i = 0; i < lineCount; i++) {
		let m = map(i, 0, 30, 0, width);
		line(m*yVol, 0, m*yVol, height);
	}
}



/* AUDIO INIT */
let mic, fftRaw, fft, waveform, amp = 0.0,
	ampEase = 0.0,
	numBins = 512,
	bands = 12;

function setupAudio() {
	mic = new p5.AudioIn();
	mic.start();
	fftRaw = new p5.FFT(0.75, numBins);
	fftRaw.setInput(mic);
}

function updateAudio() {
	fftRaw.analyze();
	amp = mic.getLevel() * 1000; // average amplitude
	ampEase = ease(amp, ampEase, 0.075); // smooth 'amp'
	waveform = fftRaw.waveform(); // array (-1, 1)
	fft = fftRaw.logAverages(fftRaw.getOctaveBands(bands)); // array (0, 255)
}
