let credits = { 
    "student_names": "Max Keller, Oliver Jauslin, Audrey Rappold", 
    "recode_artist": "Manuel Barbadillo", 
    "recode_title": "Barbadillo dairian", 
    "recode_year": 1973 
} 
function setup() {
	createCanvas(windowWidth, windowHeight);
	rectMode(CENTER);
	angleMode(DEGREES);
	setupAudio();
}

function draw() {
	/* audio vars: amp, ampEase, fft, waveform */
	updateAudio();
	background(0);
	noStroke();

	let lc = 4;

	if(mouseIsPressed){
		lc=6;
	}

	for(let i = 0; i < lc; i++) {
		for(let j = 0; j < 2; j++) {
			push();
			if(i % 2 == 0) {
				for(let k = 0; k < 2; k++){
					thing(width * .41 + (i * width / 7.7), height/4-(j * height / 2)+height*k+ampEase*1.5, 255, 0);
				}
			} else {
				translate(width, height);
				rotate(180);
				for(let l = 0; l < 2; l++) {
					thing(width * .4 + (i * width / 7.7) - width * .13, (j * height / 2) + height * l+ampEase*1.5, 0, 255);
				}
			}
			pop();
		}
	}

}

function thing(x, y, c1, c2) {

	fill(c1);
	rect(x - (width * .2) / 2, y, width * .13, height * .5);

	for(let i = 0; i < 4; i++) {
		fill(c1);

		if(i % 2 == 0) {
			fill(c2);
		}

		rect(x - (width * .2 - i * 50) / 2, y, width * .13 - i * 50+amp*.3, height * .5 - 22 * i * 4+ampEase*.1, (i + 1) * 25);
	}
}

/* AUDIO INIT */
let mic, fftRaw, fft, waveform, amp = 0.0,
	ampEase = 0.0,
	numBins = 512,
	bands = 12;

function setupAudio() {
	mic = new p5.AudioIn();
	mic.start();
	fftRaw = new p5.FFT(0.75, numBins);
	fftRaw.setInput(mic);
}

function updateAudio() {
	fftRaw.analyze();
	amp = mic.getLevel() * 1000; // average amplitude
	ampEase = ease(amp, ampEase, 0.2); // smooth 'amp'
	waveform = fftRaw.waveform(); // array (-1, 1)
	fft = fftRaw.logAverages(fftRaw.getOctaveBands(bands)); // array (0, 255)
}
