let credits = {"student_names": "firstname lastname, firstname2 lastname2","recode_artist": "Artist[s] firstname lastname","recode_title": "Name of work","recode_year": 1961 }

function setup() {
	createCanvas(windowWidth, windowHeight, WEBGL);

	setupAudio();
	noStroke();
	fill(255);
}

function draw() {
	background(0);
	lights();
	updateAudio();
	let lc = 10;
	for(let i = 0; i < lc; i++) {
		for(let j = 0; j < lc; j++) {
			let x = map(i, 0, lc - 1, -width / 2, width / 2);
			let y = map(j, 0, lc - 1, -height / 2, height / 2);
			let s = map(fft[floor(map(i, 0, lc - 1, 0, fft.length))], 0, 255, 15, 80);
			push();
			translate(x, y)
			sphere(s, 5, 5);
			pop();
		}
	}
}

/* AUDIO INIT */
let mic, fftRaw, fft, waveform, amp = 0.0,
	ampEase = 0.0,
	numBins = 512,
	bands = 12;

function setupAudio() {
	mic = new p5.AudioIn();
	mic.start();
	fftRaw = new p5.FFT(0.75, numBins);
	fftRaw.setInput(mic);
}

function updateAudio() {
	fftRaw.analyze();
	amp = mic.getLevel() * 1000; // average amplitude
	ampEase = ease(amp, ampEase, 0.075); // smooth 'amp'
	waveform = fftRaw.waveform(); // array (-1, 1)
	fft = fftRaw.logAverages(fftRaw.getOctaveBands(bands)); // array (0, 255)
}
