function setup() {
	createCanvas(windowWidth, windowHeight);

}

function draw() {
	background(0);
	fill(255);
	translate(width / 2, height / 2);
	let lc = 30;
	beginShape();
	for(let i = 0; i < lc; i++) {
		let r = height/3;
		if(i % 2 == 0)
			r = mouseY/2;
		let x = sin(i * TWO_PI / lc) * r; // i*2
		let y = cos(i * TWO_PI / lc) * r;
		vertex(x, y)
	}
	endShape();
}
