let credits = {"student_names": "firstname lastname, firstname2 lastname2","recode_artist": "Artist[s] firstname lastname","recode_title": "Name of work","recode_year": 1961 }
let px, py;

function setup() {
	createCanvas(windowWidth, windowHeight);
	stroke(255);
	setupAudio();
}

function draw() {
	/* audio vars: amp, ampEase, fft, waveform */
	updateAudio();
	strokeWeight(.5 + ampEase * .0125);
	let scl = .0000001;
	let vx = .01 + (ampEase * scl);
	let vy = .012 + (ampEase * scl);
	let x = noise(frameCount * vx) * width;
	let y = noise(100 + frameCount * vy) * height;
	line(x, y, px, py)

	px = x;
	py = y;
}
/* END RECODE */


/* AUDIO INIT */
let mic, fftRaw, fft, waveform, amp = 0.0,
	ampEase = 0.0,
	numBins = 512,
	bands = 12;

function setupAudio() {
	mic = new p5.AudioIn();
	mic.start();
	fftRaw = new p5.FFT(0.75, numBins);
	fftRaw.setInput(mic);
}

function updateAudio() {
	fftRaw.analyze();
	amp = mic.getLevel() * 1000; // average amplitude
	ampEase = ease(amp, ampEase, 0.075); // smooth 'amp'
	waveform = fftRaw.waveform(); // array (-1, 1)
	fft = fftRaw.logAverages(fftRaw.getOctaveBands(bands)); // array (0, 255)
}
