let music;

function setup() {
	createCanvas(windowWidth, windowHeight, WEBGL);
	music = new p5.AudioIn();
	music.start();
	noStroke()
	angleMode(DEGREES)
}

function draw() {
	background(0);

	let vol = music.getLevel();

	rotateY(map(mouseY, height / 2, width, 0, 10));
	rotateX(map(mouseX, 0, width, 0, 10));
	noCursor();

	const dirY = (mouseY / height - 0.5) * 4;
	const dirX = (mouseX / width - 0.5) * 4;
	ambientLight(map(vol, 0, 1, 0, 50), map(vol, 0, 1, 0, 100), map(vol, 0, 1, 0, 200));

	directionalLight(255, 40, 120, dirX, dirY, 1);
	directionalLight(255, 10, 120, dirX, dirY, 100);
	pointLight(100, 20, 50, dirX, dirY, 100);

	push();
	rotateX(90);
	translate(0, -100, 0);
	cone(50, 100, 24);
	fill(0);
	pop();

	let locX = mouseX - height / 2;
	let locY = mouseY - width / 2;

	pointLight(200, 50, 100, locX, locY, 100);
	pointLight(200, 50, 100, locY, locX, 100);

	rotateZ(frameCount * 10000000);
	rotateX(frameCount * .05);
	rotateX(map(mouseX, 0, width, 0, 180));

	push();
	scale(vol * -5 + 1);
	rotateZ(1);
	translate(-2, -2, 150)
	torus(16, 8, 24, 4);
	specularMaterial(255);
	pop();

	push();
	scale(vol * 5 + 1);
	rotate(5);
	translate(-5, -5, 50);
	torus(40, 8, 24, 4);
	specularMaterial(255);
	pop();

	push();
	scale(vol * 5 + 1);
	rotateX(5);
	translate(-3, -10, -50);
	torus(60, 15, 24, 4);
	specularMaterial(255);
	pop();

	push();
	scale(vol * 15 + 1);
	rotateZ(-5);
	translate(-10, -5, -100);
	torus(70, 20, 24, 4);
	specularMaterial(255);
	pop();

	push();
	scale(vol * -5 + 1);
	translate(0, 0, -300);
	rotateZ(frameCount * 0.01);
	rotateX(frameCount * 0.01);
	rotateY(frameCount * 0.01);
	sphere(100, 24);
	pop();
}
