let fc1 = 0;


// oval
// length lines
// whole thing gets bigger
// per sound circle one after the other
// reacts to different levels of loundness on each circle


function setup() {
	createCanvas(windowWidth, windowHeight);
	angleMode(DEGREES);
	setupAudio();



}

function draw() {
	/* audio vars: amp, ampEase, fft, waveform */
	updateAudio();
	fc1 += 51;
	background(0,5.5);
	translate(width / 2, height / 2);




	push(); //orange lines
	//rotate(100*frameCount)
	rotate(fc1 * 2);
	noFill();
	stroke(230, 92, 0);
	strokeWeight(2); // +amp
	let s = (210, 150);
	//line(0,0,s,s)
	translate(370, 200);
	//rotate(frameCount/2)
	line(-s, 4, -s, -s);
	pop();

	push(); //yellow ellipse
	rotate(fc1 * 2);
	noFill();
	ellipse(0, 2, 450, 460);
	stroke(255, 191, 0, 70);
	noFill();
	strokeWeight(2);
	ellipse(0, 2, 450+amp, 460+amp);
	pop();




	push(); //blue ellipse
	rotate(fc1 * 2);
	stroke(0, 191, 250,90);
	strokeWeight(1); // *amp
	noFill();
	ellipse(0, 0, 250, 260 + ampEase);
	pop();


	push(); //green lines
	rotate(fc1 * 47);
	//	rotate(fc1 * 57);
	noFill();
	strokeWeight(2);
	stroke(0, 204, 68);
	let d = (100, 200);
	translate(329, 160);
	line(-d, -2, -d, -d);
	pop();

	push(); //salmon lines
	rotate(frameCount * 2);
	noFill();
	stroke(255, 102, 102);
	let e = (210, 200);
	//line(0,0,s,s)
	translate(150, 120);
	line(-e, 2, -e, -e);
	pop();

	push(); //salmon ellipse
	rotate(frameCount * 2);
	noFill();
	stroke(255, 102, 102, 8);
	strokeWeight(2);
	ellipse(0, 0, 100, 100);
	pop();


	push(); //inner orange circle
	rotate(frameCount * 1);
	noStroke();
	fill(255, 51, 0, 5);
	ellipse(0, 0, 50, 80);
	//scale(1+amp)
	stroke(255, 77, 210);
	noFill();
	strokeWeight(1);
	ellipse(0, 0, 50+amp, 60+amp);
	pop();

	push(); //pink lines
	rotate(fc1 * 2);
	noFill();
	strokeWeight(2);
	stroke(255, 77, 210, 90);
	let p = (100, 40);
	translate(6, 4);
	line(-p, 1, -p, -p);
	pop();


	push(); //new circle blue
	rotate(fc1 * 2);
	stroke(0, 0, 255);
	strokeWeight(3);
	noFill();
	ellipse(10, 10, 2000-amp, 2000-amp);
	pop();



  if (mouseIsPressed) {
  clear();



  }


}



/* AUDIO INIT */
let mic, fftRaw, fft = [],
	waveform = [],
	amp = 0.0,
	ampStereo = {
		l: 0.0,
		r: 0.0
	},
	ampEase = 0.0,
	numBins = 512,
	bands = 12;

function setupAudio() {
	userStartAudio();
	mic = new p5.AudioIn();
	mic.start();
	fftRaw = new p5.FFT(0.75, numBins);
	fftRaw.setInput(mic);
}

function updateAudio() {
	fftRaw.analyze();
	amp = mic.getLevel() * 1000; // average mixed amplitude
	ampStereo.l = mic.amplitude.getLevel(0) * 500; // average left amplitude
	ampStereo.r = mic.amplitude.getLevel(1) * 500; // average right amplitude
	ampEase = ease(amp, ampEase, 0.075); // smooth 'amp'
	waveform = fftRaw.waveform(); // array (-1, 1)
	fft = fftRaw.logAverages(fftRaw.getOctaveBands(bands)); // array (0, 255)
}
