//http://dada.compart-bremen.de/item/artwork/716
let i;
let j;
let myAudio = 1;

function setup() {
	createCanvas(windowWidth, windowHeight);
	angleMode(DEGREES)

	setupAudio();
}

function draw() {
	/* audio vars: amp, ampEase, fft, waveform */
	updateAudio();

	background(255, 5);
	stroke(255);
	strokeWeight(250)
	line(mouseX, mouseY, pmouseX, pmouseY);



	fill(255);
	strokeWeight(5);
	stroke(0);

	push();
	beginShape()
	for(let j = 0; j < height; j++) {
		//line(0, j * 40, width * 15, height * -50);
		vertex(0, j * 40)
		vertex(1500 * 15, 800 * -50)
	}
	endShape()
	pop();

	let loopCount = 10;
	for(let i = 0; i < loopCount; i++) {
		fill(255)
		if(mouseIsPressed && i == loopCount - 1) { //rot
			fill(255, 0, 0)
			stroke(255, 0,0)
			
		}
		rect(width * .29 - (i * 4)- (ampEase * myAudio), height * .1 + (i * 8)-(ampEase * myAudio), (loopCount * 12) + (ampEase * myAudio), (loopCount * 5) - (ampEase * myAudio)); //1
		if(mouseIsPressed && i == loopCount - 1) { //grün
			fill(0, 255, 0)
			stroke(0, 255,0)
		}
		rect(width * .47 - (i * 4)- (ampEase * myAudio), height * .001 + (i * 8)- (ampEase * myAudio), loopCount * 5, loopCount * 9 + (ampEase * myAudio)); //2
		if(mouseIsPressed && i == loopCount - 1) { //blau
			fill(0, 0, 255)
			stroke(0,0,255)
		}
		rect(width * .58 - (i * 4) + (ampEase * myAudio), height * .1 + (i * 8) - (ampEase * myAudio), loopCount * 17 + (ampEase * myAudio), loopCount * 5) + (ampEase * myAudio); //3
		if(mouseIsPressed && i == loopCount - 1) { //türkis
			fill(63, 136, 143)
			stroke(63, 136, 143)
		}
		rect(width * .25 - (i * 4) - (ampEase * myAudio), height * .4 + (i * 8) + (ampEase * myAudio), loopCount * 5 + (ampEase * myAudio), loopCount * 15 + (ampEase * myAudio)); //6
		if(mouseIsPressed && i == loopCount - 1) { //rosa
			fill(255, 170, 170)
			stroke(255, 170, 170)
		}
		rect(width * .53 - (i * 4) + (ampEase * myAudio), height * .4 + (i * 8) - (ampEase * myAudio), loopCount * 5 + (ampEase * myAudio), loopCount * 5 + (ampEase * myAudio)) //4
		if(mouseIsPressed && i == loopCount - 1) { //helltürkis
			fill(0, 255, 204)
			stroke(0, 255, 204)
		}
		rect(width * .362 - (i * 4) + (ampEase * myAudio), height * .4 + (i * 8) + (ampEase * myAudio), loopCount * 15 + (ampEase * myAudio), loopCount * 5 + (ampEase * myAudio)); //5

		push();
		beginShape(); //1
		if(mouseIsPressed && i == loopCount - 1) { //gelb
			fill(255, 255, 0)
			stroke(255, 255, 0)
		}
		vertex(width * .362 - (i * 4)- (ampEase * myAudio), height * .1599 + (i * 8)) //1
		vertex(width * .458 - (i * 4), height * .1599 + (i * 8)); //2
		vertex(width * .458 - (i * 4), height * .1 + (i * 8)); //3
		vertex(width * .536 - (i * 4)+ (ampEase * myAudio), height * .1 + (i * 8)); //4
		vertex(width * .536 - (i * 4)+ (ampEase * myAudio), height * .1599 + (i * 8)); //5
		vertex(width * .506 - (i * 4), height * .1599 + (i * 8));
		vertex(width * .506 - (i * 4), height * .3 + (i * 8));
		vertex(width * .362 - (i * 4)- (ampEase * myAudio), height * .3 + (i * 8));
		endShape(CLOSE);
		pop();


		push();
		beginShape(); //2
		if(mouseIsPressed && i == loopCount - 1) { //gelb
			fill(255, 0, 255)
			stroke(255, 0,255)
		}
		vertex(width * .536 - (i * 4)- (ampEase * myAudio), height * .5 + (i * 10)); //pünkt vo obe links nach abe denn rechts denn ufe
		vertex(width * .536 - (i * 4) - (ampEase * myAudio), height * .64 + (i * 8) + (ampEase * myAudio));
		vertex(width * .6 - (i * 4), height * .64 + (i * 8) + (ampEase * myAudio));
		vertex(width * .6 - (i * 4), height * .58 + (i * 8));
		vertex(width * .565 - (i * 4), height * .58 + (i * 8));
		vertex(width * .565 - (i * 4), height * .5 + (i * 10));

		endShape(CLOSE);
		pop();



	}
}


/* AUDIO INIT */
let mic, fftRaw, fft, waveform, amp = 0.0,
	ampStereo = {
		l: 0.0,
		r: 0.0
	},
	ampEase = 0.0,
	numBins = 512,
	bands = 12;

function setupAudio() {
	userStartAudio();
	mic = new p5.AudioIn();
	mic.start();
	fftRaw = new p5.FFT(0.75, numBins);
	fftRaw.setInput(mic);
}

function updateAudio() {
	fftRaw.analyze();
	amp = mic.getLevel() * 1000; // average mixed amplitude
	ampStereo.l = mic.amplitude.getLevel(0) * 500; // average left amplitude
	ampStereo.r = mic.amplitude.getLevel(1) * 500; // average right amplitude
	ampEase = ease(amp, ampEase, 0.075); // smooth 'amp'
	waveform = fftRaw.waveform(); // array (-1, 1)
	fft = fftRaw.logAverages(fftRaw.getOctaveBands(bands)); // array (0, 255)
}

