let credits = {
	"student_names": " Nora Steffen, Jessica Hinze",
	"recode_artist": "Maughan Sterling Mason",
	"recode_title": "Christmas Wreath",
	"recode_year": 1968
}

function setup() {
	createCanvas(windowWidth, windowHeight);
	angleMode(DEGREES)
	noFill();
	stroke(255, 0, 100)
	setupAudio();
}

function draw() {
	updateAudio();
	background(255);

	if(mouseIsPressed) {
		background(0);
	}

	let lc = 40;
	translate(width / 2, height / 2)
	for(let i = 0; i < lc; i++) {
		let sw = .75
		let size = i * 2.8 + 180;
		if(i < 7) {
			sw = 2
		}
		if(i > 6 && i < 25) {
			sw = 1.5
		}
		wreath(size + amp * 0.7, sw)
	}

	let col = amp
	stroke(255, col, 100);
}

function wreath(size, sw, col) {
	// numPoints of circle w/ wave
	strokeWeight(sw)
	let lc = 100;
	beginShape();
	for(let i = 0; i < lc; i++) {
		let wf = waveform[floor(map(i, 0, lc - 1, 0, waveform.length))];
		let ft = fft[floor(map(i, 0, lc - 1, 0, fft.length))];
		let mapwf = map(wf, -1, 1, 0, 1)
		let r = size + noise(i * lc * 0.1 * mapwf) * 60;
		let x = sin(i * 360 / lc) * r;
		let y = cos(i * 360 / lc) * r;
		curveVertex(x, y);
	}
	endShape(CLOSE);
}

function mouseClicked() {
	background(0);
}

/* AUDIO INIT */
let mic, fftRaw, fft, waveform, amp = 0.0,
	ampEase = 0.0,
	numBins = 512,
	bands = 12;

function setupAudio() {
	mic = new p5.AudioIn();
	mic.start();
	fftRaw = new p5.FFT(0.75, numBins);
	fftRaw.setInput(mic);
}

function updateAudio() {
	fftRaw.analyze;
	amp = mic.getLevel() * 1000; // average amplitude
	ampEase = ease(amp, ampEase, 0.07); // smooth 'amp'
	waveform = fftRaw.waveform(); // array (-1, 1)
	fft = fftRaw.logAverages(fftRaw.getOctaveBands(bands)); // array (0, 255)
}
