let extraCanvas;
let my = 0;
let dir = 0;

function setup() {
	createCanvas(windowWidth, windowHeight);
	noCursor()
	setupAudio();
	extraCanvas = createGraphics(width, height);
	extraCanvas.clear()
	background(242, 240, 228);
}

function draw() {
	/* audio vars: amp, ampEase, fft, waveform */
	updateAudio();
	background(242, 240, 228);
	stroke(86, 91, 86);
	/*push()
	stroke(255, 0, 0)
	line(0, height / 2, width, height / 2)
	line(width / 2, 0, width / 2, height)
	pop()
*/

	push();
	if(mouseIsPressed) {
		//random(lone, ltwo)
	}
	extraCanvas.stroke(86 + ampEase, 91, 86)
	//extraCanvas.strokeWeight(.1+(amp/5))
	extraCanvas.strokeCap(SQUARE);
	if(dir==0){
		scale(random(2,5))
	extraCanvas.line(mouseX, my, mouseX, my +40)
	}else{
	extraCanvas.line(my, mouseY, my + 40, mouseY)
	}
	pop()

	dir
	line(mouseX, mouseY, mouseX, mouseY + 40)

	image(extraCanvas, 0, 0);


	if(mouseIsPressed) {
		textSize(10);
		fill(86, 81, 86);
		text('reconstructed by Oli Lenzlinger and Yannick Fey, 2019', width / 1.6, height / 1.04);
	} else {
		textSize(10);
		fill(86, 81, 86)
		text('Frieder Nake', width / 1.6, height / 1.04);
	}



	//i
	push()
	stroke(86, 81, 86, 20 + 5 * ampEase)
	for(var i = height / 12; i <= height / 3.18; i = i + 3) {
		line(width / 3.2, i, width / 2.48, i)
	}
	pop()

	//z
	push()
	stroke(86, 81, 86, 20)
	for(var z = width / 3.4; z <= width / 2.33; z = z + 3) {
		line(z, height / 1.61, z, height / 1.25)
	}
	pop()

	//h
	push();
	stroke(86, 91, 86, 255 - ampEase * 10);
	for(var h = width / 2.3; h <= width / 1.9; h = h + 1.5) {
		line(h, height / 13.5, h, height / 2.6)
	}
	pop();

	//k
	for(var k = height / 4.5; k <= height / 2.9; k = k + 1.5) {
		line(width / 2.3, k, width / 2.05, k)
	}

	//w
	push();
	strokeWeight(2)
	for(var w = height / 4.7; w <= height / 1.8; w = w + 4) {
		line(width / 1.9, w, width / 1.78, w)
	}
	pop();

	//f
	push();
	strokeWeight(2)
	for(var f = height / 20; f <= height / 4.6; f = f + 4.1) {
		line(width / 1.87, f, width / 1.82, f)
	}
	pop();

	//f
	push();
	strokeWeight(2)
	for(var f = height / 20; f <= height / 4.6; f = f + 4.1) {
		line(width / 1.87 + (ampEase * 3), f, width / 1.82, f)
	}
	pop();


	//s
	push();
	strokeWeight(2)
	for(var s = width / 2.5; s <= width / 1.96 - (8 * amp); s = s + 5) {
		line(s, height / 1.4, s, height / 1.05)
	}
	pop();

	//l
	for(var l = width / 2.36; l <= width / 1.86; l = l + 3) {
		line(l, height / 2.85, l, height / 1.5)
	}

	//a
	push();
	strokeWeight(2)
	for(var a = width / 1.71; a <= width / 1.54; a = a + 4) {
		line(a, height / 1.5, a, height / 1.28)
	}
	pop();

	//b
	push();
	strokeWeight(2)

	for(var b = height / 1.7; b <= height / 1.08; b = b + 5) {
		line(width / 1.797, b, width / 1.59, b)
	}
	pop();

	//b2
	push();
	strokeWeight(2)
	stroke(86 + (ampEase * 2), 91 - ampEase, 86 - ampEase, 255 - ampEase * 2);
	for(var b = height / 1.7; b <= height / 1.18 * ampEase / 10; b = b + 5) {
		line(width / 4, b, width / 5, b)
	}
	pop();


	//b3
	push();
	fill(255, 50, 50, ampEase * 10)
	noStroke();
	rect(width / 3.5, height / 4, width / 10, height / 5)
	pop();


	//v
	push();
	fill(86 + (ampEase * 10), 91 - ampEase, 86 - ampEase, 255 - ampEase * 1.1)
	noStroke();
	rect(width / 1.821, height / 2.35, width / 20, height / 3.5)
	pop();

	//n
	push();
	fill(0, 40);
	noStroke();
	rect(width / 2.33 - ampEase, height / 2.55, width / 30, height / 3);
	pop();

	//j
	push();
	fill(0, 50 - ampEase * 3);
	noStroke();
	rect(width / 2.4, height / 3.7, width / 25, height / 5);
	pop();

	//m
	push();
	fill(86, 91, 86, 200 - ampEase * 2);
	noStroke();
	rect(width / 2.91, height / 2.3, width / 12, height / 7);
	pop();

	//m2
	push();
	fill(86, 91, 86, .1 * ampEase * 10);
	noStroke();
	rect(width / 4.5, height / 3, width / 7, height / 3);
	pop();

	//m3
	push();
	fill(86, 91, 86, .1 * ampEase * 20);
	noStroke();
	rect(width / 1.7, height / 3, width / 3.5, height / 3 - (ampEase * 2));
	pop();

	//m4
	push();
	fill(255, 50, 50, -255 + ampEase * 2 + 100);
	noStroke();
	rect(width / 1.4, height / 2.8, width / 6, height / 3);
	pop();


	//c
	push();
	fill(86 + (ampEase), 91 - ampEase, 86 - ampEase, 255 - ampEase * 2);
	noStroke();
	rect(width / 1.66, height / 2.85, width / 100, height / 4);
	pop();

	//d
	push();
	fill(86 + (ampEase * 2), 91 - ampEase, 86 - ampEase, 255 - ampEase * 2);
	noStroke();
	rect(width / 1.626, height / 5, width / 50, height / 3.3);
	pop();

	//e
	push();
	fill(86, 91, 86, 220);
	noStroke();
	rect(width / 1.72, height / 6.5, width / 40, height / 6.5);
	pop();

	//e2
	push();
	fill(86, 91, 86, 220);
	noStroke();
	rect(width / 1.72, height / 6.492, width / 40, -ampEase);
	pop();


	//q
	push();
	fill(86 + (ampEase * 2), 91 - ampEase, 86 - ampEase, 255 - ampEase * 3);
	noStroke();
	rect(width / 3.2, height / 1.5, width / 11, height / 90);
	pop();

	//q2
	push();
	fill(86 + (ampEase * 2), 91 - ampEase, 86 - ampEase, 255 - ampEase * 3);
	noStroke();
	rect(width / 3.2, height / 1.5, -width / 30 * ampEase, height / 90);
	pop();


	//r
	push();
	fill(86, 91, 86);
	noStroke();
	rect(width / 2.34, height / 1.52, width / 90, height / 5);
	pop();

	//x
	push();
	fill(86, 91, 86, 150);
	noStroke();
	rect(width / 1.83 + (ampEase), height / 5.98, width / 25, height / 2 - (ampEase * 1));
	pop();

	//g
	push();
	fill(86, 91, 86, 20);
	noStroke();
	rect(width / 1.894, height / 12, width / 30, height / 5);
	pop();

	//p
	push();
	fill(86, 91, 86, 8);
	noStroke();
	rect(width / 3, height / 1.6, width / 10, height / 13);
	pop();

	//z1
	push();
	fill(86, 91, 86, 8);
	noStroke();
	rect(width / 2.1, height / 1.37, width / 10, height / 30);
	pop();

	//z2
	push();
	fill(86, 91, 86, 8);
	noStroke();
	rect(width / 2.1, height / 1.15, width / 10, height / 90);
	pop();

	//u
	push();
	fill(86, 91, 86, 200);
	noStroke();
	rect(width / 1.78, height / 1.5, width / 400, height / 6);
	pop();

	//u1
	push();
	fill(86, 91, 86, ampEase * 10);
	noStroke();
	rect(width / 7, height / 3.5, width / 2, height / 400);
	pop();




}

function mousePressed() {
	my = mouseY;
	dir = round(random(1))
}

function keyPressed( ) {
	extraCanvas.clear()
}




/* AUDIO INIT */
let mic, fftRaw, fft = [],
	waveform = [],
	amp = 0.0,
	ampStereo = {
		l: 0.0,
		r: 0.0
	},
	ampEase = 0.0,
	numBins = 512,
	bands = 12;

function setupAudio() {
	userStartAudio();
	mic = new p5.AudioIn();
	mic.start();
	fftRaw = new p5.FFT(0.75, numBins);
	fftRaw.setInput(mic);
}

function updateAudio() {
	fftRaw.analyze();
	amp = mic.getLevel() * 1000; // average mixed amplitude
	ampStereo.l = mic.amplitude.getLevel(0) * 500; // average left amplitude
	ampStereo.r = mic.amplitude.getLevel(1) * 500; // average right amplitude
	ampEase = ease(amp, ampEase, 0.075); // smooth 'amp'
	waveform = fftRaw.waveform(); // array (-1, 1)
	fft = fftRaw.logAverages(fftRaw.getOctaveBands(bands)); // array (0, 255)
}
